mapper.update:
UPDATE THEATER
SET THEATER_NAME = #{THEATER_NAME}, THEATER_INTRO1 =  #{THEATER_INTRO1}, THEATER_INTRO2 = #{THEATER_INTRO2},
THEATER_POST = #{THEATER_POST}, THEATER_ADDRESS1 = #{THEATER_ADDRESS1}, THEATER_ADDRESS2 = #{THEATER_ADDRESS2}, 
THEATER_LOCATION = #{THEATER_LOCATION}, THEATER_PARKING =  #{THEATER_PARKING}, THEATER_DISABLED_SEAT = #{THEATER_DISABLED_SEAT},
THEATER_SPECIAL_SEAT = #{THEATER_SPECIAL_SEAT}
WHERE THEATER_ID = #{THEATER_ID}
