mapper.select:
SELECT COUNT(*)
FROM THEATER_ROOM
WHERE THEATER_ID = #{theaterId}

mapper.update:
UPDATE THEATER_ROOM
SET THEATER_ROOM_NAME = #{THEATER_ROOM_NAME}, THEATER_ROOM_SEAT_TYPE = #{THEATER_ROOM_SEAT_TYPE}
WHERE THEATER_ROOM_ID = #{THEATER_ROOM_ID}

mapper.select:
SELECT THEATER_ID
FROM THEATER
WHERE THEATER_MANAGER_ID = #{managerId}
